import { MainLayout } from '../../components/MainLayout';
import styled from 'styled-components';
import { Row, Col } from 'antd';
import { queryEnum, RecipesList } from '../../components/RecipesList';
import { useFetchUser } from '../../utils/user';
import { get as $get } from 'lodash';

const StyledRow = styled(Row)`
  ${({theme}) => `
    padding: ${theme['padding-small']}
    display: flex;
    h1 {
      padding-left: ${theme['padding-small']}
      text-align: left;

    }
  `}
`

const MyRecipes = () => {
  const {user, loading} = useFetchUser();
  const owner = $get(user, 'sub');
  const options = owner ? {variables: {where: {owner}}} : {};

  return (
    <MainLayout title='My recipes'>
      <StyledRow>
        <Col span={24}>
          <h1>My Recipes</h1>
        </Col>
      </StyledRow>
      <div>
        <RecipesList 
          parentRoute='my-recipes' 
          queryType={queryEnum.recipes} 
          options={options} 
        />
      </div>
    </MainLayout>
  )
}

export default MyRecipes;
