import {createGlobalStyle} from 'styled-components';

const heading = size => {
  return `
    text-align: center;

    line-height: 1em;
    font-size: ${size}
  `
}

export const GlobalStyle = createGlobalStyle`
  ${({theme}) => `
    h1 {
      ${heading(theme['font-size-xl'])}
    }
    h2 {
      ${heading(theme['font-size-lg'])}
    }
    h3 {
      ${heading(theme['font-size-md'])}
    }
    h4 {
      ${heading(theme['font-size-sm'])}
    }
  `}
`
