import {QueryHookOptions, useQuery} from '@apollo/react-hooks';
import {recipesGraphQL} from '../../graphql/queries/recipies';
import {userLikesGraphQL} from '../../graphql/queries/userLikes';

export enum queryEnum {
  userLikes = 'userLikes',
  recipes = 'recipes'
}

type RecipesListProps = {
  options?: QueryHookOptions;
  parentRoute: string;
  queryType: queryEnum;
}

export const RecipesList = ({
  options,
  parentRoute,
  queryType
}: RecipesListProps) => {
  const query = 
    queryType === queryEnum.recipes 
      ? recipesGraphQL 
      : userLikesGraphQL;

  const {data, loading, error} = useQuery(query, options);

  if(loading) return <p>Loading</p>

  if (error || !RecipesList) return <p>Error</p>

  return <p>R test </p>
}
